var elements = document.getElementsByClassName( 'has-entrance-animation' );
function inView( e ) {
	var n = window.innerHeight,
		t = window.scrollY || window.pageYOffset;
	return t + n > e.getBoundingClientRect().top + t + e.clientHeight;
}
function animate() {
	for ( let e = 0; e < elements.length; e++ )
		inView( elements[ e ] ) &&
			elements[ e ].classList.remove( 'has-entrance-animation' );
}
document.addEventListener( 'scroll', animate );
