function inView(n){var e=window.innerHeight,t=window.scrollY||window.pageYOffset;return t+e>n.getBoundingClientRect().top+t+n.clientHeight}function animate(){const n=document.getElementsByClassName("has-entrance-animation");Array.from(n).forEach(n=>{inView(n)&&n.classList.remove("has-entrance-animation")})}document.addEventListener("scroll",animate),window.addEventListener("load",animate);