window.addEventListener("load",function(){const e=document.getElementsByClassName("wp-block-grigora-kit-faq");for(var t=0;t<e.length;t++){e[t].dataset.id;let n=e[t].dataset.faqs;e[t].dataset.closedicon,e[t].dataset.openedicon;n=JSON.parse(n);for(let e=0;e<n.length;e++)document.getElementById(n[e].id).addEventListener("click",function(){n[e].hide?(n[e].hide=!1,document.getElementById(n[e].id+"-answer").innerHTML="<div class='faq-answer'> "+n[e].answer+" </div>"):(n[e].hide=!0,document.getElementById(n[e].id+"-answer").innerHTML="")})}});