jQuery( document ).ready( function ( e ) {
	jQuery( 'input#grigora_st_media_manager' ).click( function ( e ) {
		var t;
		e.preventDefault(),
			t && t.open(),
			( t = wp.media( {
				title: 'Select Media',
				multiple: ! 1,
				library: { type: 'image' },
			} ) ).on( 'close', function () {
				var e = t.state().get( 'selection' ),
					a = new Array(),
					i = 0;
				e.each( function ( e ) {
					( a[ i ] = e.id ), i++;
				} );
				var n = a.join( ',' );
				jQuery( 'input#grigora_st_image_id' ).val( n ),
					Refresh_Image( n );
			} ),
			t.on( 'open', function () {
				var e = t.state().get( 'selection' );
				jQuery( 'input#grigora_st_image_id' )
					.val()
					.split( ',' )
					.forEach( function ( t ) {
						var a = wp.media.attachment( t );
						a.fetch(), e.add( a ? [ a ] : [] );
					} );
			} ),
			t.open();
	} );
} );
