window.addEventListener("load",function(){const e=document.getElementsByClassName("grigora-kit-social-share");for(let t=0;t<e.length;t++){let o=e[t].dataset.textshare,a=e[0].children;for(var n=0;n<a.length;n++){let e=n;a[n].addEventListener("click",()=>{let n=window.location.href,t=document.title,s=o;a[e].className.includes("whatsapp")?window.open("https://api.whatsapp.com/send?text="+encodeURIComponent(s)+" "+encodeURIComponent(n),"_blank"):a[e].className.includes("facebook")?window.open("https://www.facebook.com/sharer/sharer.php?u="+encodeURIComponent(n),"_blank"):a[e].className.includes("twitter")?window.open("https://twitter.com/intent/tweet?url="+encodeURIComponent(n)+"&text="+encodeURIComponent(s),"_blank"):a[e].className.includes("instagram")?window.open("https://www.instagram.com/?url="+encodeURIComponent(n),"_blank"):a[e].className.includes("linkedin")?window.open("https://www.linkedin.com/sharing/share-offsite/?url="+encodeURIComponent(n),"_blank"):a[e].className.includes("pinterest")?window.open("https://pinterest.com/pin/create/button/?url="+encodeURIComponent(n)+"&description="+encodeURIComponent(s),"_blank"):a[e].className.includes("reddit")?window.open("https://reddit.com/submit?url="+encodeURIComponent(n)+"&title="+encodeURIComponent(t),"_blank"):a[e].className.includes("telegram")?window.open("https://telegram.me/share/url?url="+encodeURIComponent(n)+"&text="+encodeURIComponent(s),"_blank"):a[e].className.includes("snapchat")&&window.open("https://www.snapchat.com/add/?url="+encodeURIComponent(n),"_blank")})}}});